mapper.insert:
INSERT
INTO EMPLOYEE
(
EMP_NAME
, EMP_NO
, EMAIL
, PHONE
, JOB_CODE
, SAL_LEVEL
)
VALUES
(
#{ empName }
,#{ empNo }
,#{ email }
,#{ phone }
,#{ jobCode }
,#{ salLevel }
)

mapper.update:
UPDATE EMPLOYEE
SET EMP_NAME = #{ empName }
, EMP_NO = #{ empNo }
, EMAIL = #{ email }
, PHONE = #{ phone }
, JOB_CODE = #{ jobCode }
, SAL_LEVEL = #{ empNo }
, SALARY = #{ empNo }
, BONUS = #{ bonus }
, MANAGER_ID = #{ managerId }
, HIRE_DATE = #{ hireDate }
, ENT_DATE = #{ entDate }
, EN_YN = #{ entYn }

mapper.delete:
